import { Link,useNavigate } from 'react-router-dom';
import React from 'react';
import { useState } from 'react';

function Login(){
  const navigate = useNavigate();
  navigate("/home",{replace:true});
}

async function LoginForm({Login, error}) {
    const [details, setDetails] = useState({name:"",user:"", password:""});
    const submitHandler = e => {
        e.preventDefault();
        console.log(details)
        const {name, user, password } = details;
        fetch("http://localhost:9000/login",{
          method:"POST",
          crossDomain:true,
          headers:{
          "content-type":"application/json",
          Accept:"application/json",
          "Access-Control-Allow-Origin":"*",
          },
          body:JSON.stringify({
            name,
            user,
            password,
            }),
        }).then((res)=>res.json())
        .then((data)=> {
          console.log(data,'userRegister')
        if(data.login == true){
          const navigate = useNavigate();
          navigate('/home', {replace:true});
        }
        });
        //App();
        //Login(details);
    }
    return(

        <div className='container'>
    <h1 className="loginHeading">Login Page</h1>
    <div className='loginDiv'>
      <div className='formDiv'>
          <form onSubmit={submitHandler} >
            <label htmlFor="User">
              UserName:
            </label>
             <input type="text" name='user' id="user" value={details.user}
             onChange={e => setDetails({...details, user: e.target.value})}/>
            <label htmlFor='password'>Password: </label>
             <input type="password" name="password" id="password" value={details.password} 
             onChange={e => setDetails({...details, password: e.target.value})}/>
            <input  type='submit' value='Submit'/>
            <p className="BTL"><Link to="/register">Register New User</Link></p>
          </form>
      </div>
    </div>
    </div>


    )
}

export default LoginForm;
