import { Link, BrowserRouter, Route, Routes, useNavigate } from 'react-router-dom';
import App from '../App';
import Home from '../Home';
import React from 'react';
import { useState } from 'react';


function LoginForm() {
    const [user, setUser] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [loggedIn, setLogin] = useState(false);
    const navigate = useNavigate();
    //const [details, setDetails] = useState({name:"",user:"", password:"",loggedIn:false});
    const submitHandler = async (e) => {
        e.preventDefault();
        //console.log(details)
        //const {name, user, password, loggedIn } = details;
        let result = await fetch("http://localhost:9000/login",{
          method:"POST",
          crossDomain:true,
          headers:{
          "content-type":"application/json",
          Accept:"application/json",
          "Access-Control-Allow-Origin":"*",
          },
          body:JSON.stringify({
            name,
            user,
            password,
            loggedIn,
            })
        })//.then((res)=>res.json())
          //.then((data)=> {
          //console.log(data,'userRegistered')
          result = await result.json()
          console.warn(result);
          if(result.name){
            localStorage.setItem('user',JSON.stringify(result));
            //return(<Route path="/home" element={<Home name="top"/>} />) 
            navigate('home/top',<Home name="top" />); 
            //console.log(result);
            //setDetails({...details,loggedIn:data.login,user:data.user,name:data.name,})
            //console.log(data,'userRegister');
            //Navigate("/home",{replace:true});
            //window.location.replace("/home")
            //return(<> <App name={details.name}  /> </>)
        }else{alert("Please enter correct details")}
        //});
        //Login(details);
    }
    return(

    <div className='container'>
      <h1 className="loginHeading">Login Page</h1>
      <div className='loginDiv'>
        <div className='formDiv'>
            <form onSubmit={submitHandler}>
              <label htmlFor="User">
                UserName:
              </label>
               <input type="text" name='user' id="user" value={user}
               onChange={e => setUser( e.target.value)}/>
              <label htmlFor='password'>Password: </label>
               <input type="password" name="password" id="password" value={password} 
               onChange={e => setPassword( e.target.value)}/>
              <input  type='submit' value='Submit'/>
              <p className="BTL"><Link to="/register">Register New User</Link></p>
            </form>
         </div>
      </div>
    </div>


    )
}

export default LoginForm;
