import React from 'react';
import { useState } from 'react';
import App from '../App';
function LoginForm({Login, error}) {
    const [details, setDetails] = useState({user:"", password:""});
    const submitHandler = e => {
        e.preventDefault();
        //App();
        Login(details);
    }
    return(

        <div className='container'>
    <h1 className="loginHeading">Login Page</h1>
    <div className='loginDiv'>
      <div className='formDiv'>
          <form onSubmit={submitHandler} >
            <label htmlFor="User">
              UserName:
            </label>
             <input type="text" name='user' id="user" value={details.user}
             onChange={e => setDetails({...details, user: e.target.value})}/>
            <label htmlFor='password'>Password: </label>
             <input type="password" name="password" id="password" value={details.password} 
             onChange={e => setDetails({...details, password: e.target.value})}/>
            <input  type='submit' value='Submit'/>
          </form>
      </div>
    </div>
    </div>


    )
}

export default LoginForm;
